import React from 'react';
import { PlayIcon, FlagIcon } from '@heroicons/react/24/solid';

const Matrix = ({
  rows, 
  cols, 
  startPos, 
  targetPos, 
  walls, 
  onCellClick, 
  onMouseDown, 
  onMouseEnter, 
  onMouseUp, 
  dragMode,
  visitedNodes = [],
  pathNodes = [],
  isRunning = false
}) => {
  // Calculate cell type (empty, wall, start, or target)
  const getCellType = (row, col) => {
    if (row === startPos.row && col === startPos.col) return 'start';
    if (row === targetPos.row && col === targetPos.col) return 'target';
    if (walls.has(`${row}-${col}`)) return 'wall';

    // Check if this cell is part of the final path
    if (pathNodes.some(node => node.row === row && node.col === col)) {
      return 'path';
    }

    // Check if this cell was visited during algorithm execution
    if (visitedNodes.some(node => node.row === row && node.col === col)) {
      return 'visited';
    }

    return 'empty';
  };

  // Get appropriate CSS class based on cell type
  const getCellClass = (row, col) => {
    const type = getCellType(row, col);
    const isEvenCell = (row + col) % 2 === 0;

    let cellClass = "flex items-center justify-center min-w-5 min-h-5 w-full h-full transition-all duration-200 ";

    switch (type) {
      case 'empty':
        cellClass += isEvenCell 
          ? "bg-gray-800 hover:bg-gray-700" 
          : "bg-gray-900 hover:bg-gray-800";
        break;
      case 'wall':
        cellClass += "bg-indigo-800 hover:bg-indigo-700";
        break;
      case 'start':
        cellClass += "bg-emerald-600 hover:bg-emerald-500";
        break;
      case 'target':
        cellClass += "bg-amber-600 hover:bg-amber-500";
        break;
      case 'visited':
        cellClass += "bg-blue-800/70 hover:bg-blue-700/70 animate-pulse";
        break;
      case 'path':
        cellClass += "bg-purple-600 hover:bg-purple-500 animate-pulse";
        break;
      default:
        cellClass += isEvenCell 
          ? "bg-gray-800 hover:bg-gray-700" 
          : "bg-gray-900 hover:bg-gray-800";
    }

    return cellClass;
  };

  // Render the content of a cell (icons for start/target nodes)
  const renderCellContent = (row, col) => {
    const type = getCellType(row, col);
    if (type === 'start') {
      return (
        <div className="absolute inset-0 flex items-center justify-center">
          <PlayIcon className="w-4 h-4 text-white drop-shadow-lg" />
        </div>
      );
    }
    if (type === 'target') {
      return (
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="h-2 w-2 bg-white rounded-full"></div>
        </div>
      );
    }
    return null;
  };

  // Create all cells for the grid
  const renderCells = () => {
    const cells = [];

    for (let row = 0; row < gridSize; row++) {
      for (let col = 0; col < gridSize; col++) {
        cells.push(
          <div
            key={`${row}-${col}`}
            className={`${getCellClass(row, col)} ${dragMode && getCellType(row, col) === 'empty' ? 'hover:bg-blue-500/20' : ''}`}
            onClick={() => onCellClick(row, col)}
            onMouseDown={() => onMouseDown(row, col)}
            onMouseEnter={() => onMouseEnter(row, col)}
            onMouseUp={onMouseUp}
            onDragStart={(e) => e.preventDefault()}
            style={{
              cursor: getCellType(row, col) === 'start' || getCellType(row, col) === 'target' 
                ? 'grab' 
                : dragMode ? 'crosshair' : 'pointer'
            }}
          >
            {renderCellContent(row, col)}
          </div>
        );
      }
    }

    return cells;
  };

  // Render the matrix
  return (
    <div 
      className="grid gap-[1px] bg-gray-700 p-[1px] rounded-md overflow-hidden shadow-lg w-full h-full"
      style={{ 
        gridTemplateColumns: `repeat(${gridSize}, minmax(0, 1fr))`,
        gridTemplateRows: `repeat(${gridSize}, minmax(0, 1fr))`,
        aspectRatio: '1/1',
        width: '100%',
        height: '100%',
        maxHeight: '100%'
      }}
    >
      {renderCells()}
    </div>
  );
};

export default Matrix;
