import React from 'react';
import { PlayIcon, FlagIcon } from '@heroicons/react/24/solid';

const Matrix = ({
  gridSize,
  startPos,
  targetPos,
  walls,
  onCellClick,
  onMouseDown,
  onMouseEnter,
  onMouseUp,
  dragMode
}) => {
  const getCellType = (row, col) => {
    if (row === startPos.row && col === startPos.col) return 'start';
    if (row === targetPos.row && col === targetPos.col) return 'target';
    if (walls.has(`${row}-${col}`)) return 'wall';
    return 'empty';
  };

  const getCellClass = (row, col) => {
    const type = getCellType(row, col);
    let baseClass = 'matrix-cell';

    switch (type) {
      case 'start':
        return `${baseClass} start`;
      case 'target':
        return `${baseClass} target`;
      case 'wall':
        return `${baseClass} wall`;
      default:
        return baseClass;
    }
  };

  const renderCellContent = (row, col) => {
    const type = getCellType(row, col);

    if (type === 'start') {
      return (
        <div className="absolute inset-0 flex items-center justify-center">
          <PlayIcon className="w-5 h-5 text-white drop-shadow-lg" />
        </div>
      );
    }

    if (type === 'target') {
      return (
        <div className="absolute inset-0 flex items-center justify-center">
          <FlagIcon className="w-5 h-5 text-white drop-shadow-lg" />
        </div>
      );
    }

    return null;
  };

  const handleCellInteraction = (row, col, event) => {
    event.preventDefault();

    if (event.type === 'mousedown') {
      onMouseDown(row, col);
    } else if (event.type === 'click') {
      onCellClick(row, col);
    } else if (event.type === 'mouseenter') {
      onMouseEnter(row, col);
    }
  };

  return (
    <div 
      className="inline-block bg-gray-900/50 p-6 rounded-2xl border border-gray-700 backdrop-blur-sm"
      onMouseUp={onMouseUp}
      onMouseLeave={onMouseUp}
    >
      <div 
        className="grid gap-0.5 select-none"
        style={{ 
          gridTemplateColumns: `repeat(${gridSize}, minmax(0, 1fr))`,
          gridTemplateRows: `repeat(${gridSize}, minmax(0, 1fr))`
        }}
      >
        {Array.from({ length: gridSize }, (_, row) =>
          Array.from({ length: gridSize }, (_, col) => (
            <div
              key={`${row}-${col}`}
              className={`${getCellClass(row, col)} ${
                dragMode && (
                  (dragMode === 'start' && getCellType(row, col) !== 'start') ||
                  (dragMode === 'target' && getCellType(row, col) !== 'target')
                ) ? 'ring-2 ring-blue-400 ring-opacity-50' : ''
              }`}
              onClick={(e) => handleCellInteraction(row, col, e)}
              onMouseDown={(e) => handleCellInteraction(row, col, e)}
              onMouseEnter={(e) => handleCellInteraction(row, col, e)}
              style={{
                cursor: dragMode ? 'crosshair' : 
                       getCellType(row, col) === 'start' || getCellType(row, col) === 'target' ? 'grab' : 'pointer'
              }}
            >
              {renderCellContent(row, col)}

              {/* Hover effect indicator */}
              {dragMode && getCellType(row, col) === 'empty' && (
                <div className="absolute inset-0 bg-blue-400 opacity-20 rounded-sm"></div>
              )}
            </div>
          ))
        )}
      </div>

      {/* Grid coordinates (optional, for debugging) */}
      <div className="mt-4 text-center text-xs text-gray-500">
        Start: ({startPos.row}, {startPos.col}) | Target: ({targetPos.row}, {targetPos.col})
      </div>
    </div>
  );
};

export default Matrix;
