import React, { useState, useCallback } from 'react';
import { PlayIcon, StopIcon, ArrowPathIcon } from '@heroicons/react/24/solid';
import Matrix from './components/Matrix';

const GRID_SIZE = 20;

function App() {
  const [startPos, setStartPos] = useState({ row: 2, col: 2 });
  const [targetPos, setTargetPos] = useState({ row: 17, col: 17 });
  const [walls, setWalls] = useState(new Set());
  const [isDrawing, setIsDrawing] = useState(false);
  const [dragMode, setDragMode] = useState(null); // 'start', 'target', or null

  const handleCellClick = useCallback((row, col) => {
    if (dragMode === 'start') {
      setStartPos({ row, col });
      setDragMode(null);
    } else if (dragMode === 'target') {
      setTargetPos({ row, col });
      setDragMode(null);
    } else if (row === startPos.row && col === startPos.col) {
      // Clicked on start node
      return;
    } else if (row === targetPos.row && col === targetPos.col) {
      // Clicked on target node
      return;
    } else {
      // Toggle wall
      const key = `${row}-${col}`;
      setWalls(prev => {
        const newWalls = new Set(prev);
        if (newWalls.has(key)) {
          newWalls.delete(key);
        } else {
          newWalls.add(key);
        }
        return newWalls;
      });
    }
  }, [dragMode, startPos, targetPos]);

  const handleMouseDown = useCallback((row, col) => {
    if (row === startPos.row && col === startPos.col) {
      return;
    } else if (row === targetPos.row && col === targetPos.col) {
      return;
    } else {
      setIsDrawing(true);
      const key = `${row}-${col}`;
      setWalls(prev => {
        const newWalls = new Set(prev);
        if (!newWalls.has(key)) {
          newWalls.add(key);
        }
        return newWalls;
      });
    }
  }, [startPos, targetPos]);

  const handleMouseEnter = useCallback((row, col) => {
    if (isDrawing && row !== startPos.row && col !== startPos.col && 
        row !== targetPos.row && col !== targetPos.col) {
      const key = `${row}-${col}`;
      setWalls(prev => {
        const newWalls = new Set(prev);
        newWalls.add(key);
        return newWalls;
      });
    }
  }, [isDrawing, startPos, targetPos]);

  const handleMouseUp = useCallback(() => {
    setIsDrawing(false);
  }, []);

  const clearWalls = () => {
    setWalls(new Set());
  };

  const resetGrid = () => {
    setWalls(new Set());
    setStartPos({ row: 2, col: 2 });
    setTargetPos({ row: 17, col: 17 });
    setDragMode(null);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-matrix-bg via-gray-900 to-matrix-bg">
