import React from 'react';
import { PlayIcon, FlagIcon } from '@heroicons/react/24/solid';

const Matrix = ({
  gridSize,
  startPos,
  targetPos,
  walls,
  onCellClick,
  onMouseDown,
  onMouseEnter,
  onMouseUp,
  dragMode
}) => {
  const getCellType = (row, col) => {
    if (row === startPos.row && col === startPos.col) return 'start';
    if (row === targetPos.row && col === targetPos.col) return 'target';
    if (walls.has(`${row}-${col}`)) return 'wall';
    return 'empty';
  };

  const getCellClass = (row, col) => {
    const type = getCellType(row, col);
    let className = 'relative w-full h-full';

    if (type === 'start') className += ' bg-emerald-500 shadow-emerald-500/50 shadow-inner';
    else if (type === 'target') className += ' bg-amber-500 shadow-amber-500/50 shadow-inner';
    else if (type === 'wall') className += ' bg-gray-600 shadow-gray-600/50 shadow-inner';
    else className += ' bg-[#2C3648]';

    return className;
  };

  const renderCellContent = (row, col) => {
    const type = getCellType(row, col);
    if (type === 'start') {
      return (
        <div className="absolute inset-0 flex items-center justify-center">
          <PlayIcon className="w-4 h-4 text-white drop-shadow-lg" />
        </div>
      );
    }
    if (type === 'target') {
      return (
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="h-2 w-2 bg-white rounded-full"></div>
        </div>
      );
    }
    return null;
  };

  const handleCellInteraction = (row, col, event) => {
    event.preventDefault();

    if (event.type === 'mousedown') {
      onMouseDown(row, col);
    } else if (event.type === 'click') {
      onCellClick(row, col);
    } else if (event.type === 'mouseenter') {
      onMouseEnter(row, col);
    }
  };

  return (
    <div className="flex flex-col items-center">
      <div 
        className="grid gap-[1px] bg-gray-700 p-[1px] rounded-md overflow-hidden shadow-lg"
        style={{ 
          gridTemplateColumns: `repeat(${gridSize}, minmax(0, 1fr))`,
          gridTemplateRows: `repeat(${gridSize}, minmax(0, 1fr))`,
          width: '100%',
          aspectRatio: '1/1',
          maxWidth: '500px',
        }}
      >
        {Array.from({ length: gridSize }, (_, row) =>
          Array.from({ length: gridSize }, (_, col) => (
            <div
              key={`${row}-${col}`}
              className={`${getCellClass(row, col)} ${dragMode && getCellType(row, col) === 'empty' ? 'hover:bg-blue-500/20' : ''}`}
              onClick={(e) => handleCellInteraction(row, col, e)}
              onMouseDown={(e) => handleCellInteraction(row, col, e)}
              onMouseEnter={(e) => handleCellInteraction(row, col, e)}
              onMouseUp={onMouseUp}
              onDragStart={(e) => e.preventDefault()}
              style={{
                cursor: dragMode ? 'crosshair' : 
                       getCellType(row, col) === 'start' || getCellType(row, col) === 'target' ? 'grab' : 'pointer'
              }}
            >
              {renderCellContent(row, col)}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Matrix;
