import React, { useState, useCallback } from 'react';
import { PlayIcon, StopIcon, ArrowPathIcon } from '@heroicons/react/24/solid';
import Matrix from './components/Matrix';

const GRID_SIZE = 20;

function App() {
  const [startPos, setStartPos] = useState({ row: 2, col: 2 });
  const [targetPos, setTargetPos] = useState({ row: 17, col: 17 });
  const [walls, setWalls] = useState(new Set());
  const [isDrawing, setIsDrawing] = useState(false);
  const [dragMode, setDragMode] = useState(null); // 'start', 'target', or null

  const handleCellClick = useCallback((row, col) => {
    if (dragMode === 'start') {
      setStartPos({ row, col });
      setDragMode(null);
    } else if (dragMode === 'target') {
      setTargetPos({ row, col });
      setDragMode(null);
    } else if (row === startPos.row && col === startPos.col) {
      // Clicked on start node
      return;
    } else if (row === targetPos.row && col === targetPos.col) {
      // Clicked on target node
      return;
    } else {
      // Toggle wall
      const key = `${row}-${col}`;
      setWalls(prev => {
        const newWalls = new Set(prev);
        if (newWalls.has(key)) {
          newWalls.delete(key);
        } else {
          newWalls.add(key);
        }
        return newWalls;
      });
    }
  }, [dragMode, startPos, targetPos]);

  const handleMouseDown = useCallback((row, col) => {
    if (row === startPos.row && col === startPos.col) {
      return;
    } else if (row === targetPos.row && col === targetPos.col) {
      return;
    } else {
      setIsDrawing(true);
      const key = `${row}-${col}`;
      setWalls(prev => {
        const newWalls = new Set(prev);
        if (!newWalls.has(key)) {
          newWalls.add(key);
        }
        return newWalls;
      });
    }
  }, [startPos, targetPos]);

  const handleMouseEnter = useCallback((row, col) => {
    if (isDrawing && row !== startPos.row && col !== startPos.col && 
        row !== targetPos.row && col !== targetPos.col) {
      const key = `${row}-${col}`;
      setWalls(prev => {
        const newWalls = new Set(prev);
        newWalls.add(key);
        return newWalls;
      });
    }
  }, [isDrawing, startPos, targetPos]);

  const handleMouseUp = useCallback(() => {
    setIsDrawing(false);
  }, []);

  const clearWalls = () => {
    setWalls(new Set());
  };

  const resetGrid = () => {
    setWalls(new Set());
    setStartPos({ row: 2, col: 2 });
    setTargetPos({ row: 17, col: 17 });
    setDragMode(null);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-matrix-bg via-gray-900 to-matrix-bg p-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-white mb-2 bg-gradient-to-r from-start-node to-target-node bg-clip-text text-transparent">
            Matrix Visualizer
          </h1>
          <p className="text-gray-400 text-lg">
            Interactive 2D grid with movable start and target nodes
          </p>
        </div>

        {/* Controls */}
        <div className="bg-gray-800/50 backdrop-blur-sm rounded-2xl p-6 mb-6 border border-gray-700">
          <div className="flex flex-wrap items-center justify-center gap-4">
            <button
              onClick={() => setDragMode(dragMode === 'start' ? null : 'start')}
              className={`flex items-center gap-2 px-4 py-2 rounded-lg font-medium transition-all duration-200 ${
                dragMode === 'start'
                  ? 'bg-start-node text-white shadow-glow-green'
                  : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
              }`}
            >
              <div className="w-3 h-3 bg-start-node rounded-full"></div>
              Move Start
            </button>

            <button
              onClick={() => setDragMode(dragMode === 'target' ? null : 'target')}
              className={`flex items-center gap-2 px-4 py-2 rounded-lg font-medium transition-all duration-200 ${
                dragMode === 'target'
                  ? 'bg-target-node text-white shadow-glow-amber'
                  : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
              }`}
            >
              <div className="w-3 h-3 bg-target-node rounded-full"></div>
              Move Target
            </button>

            <button
              onClick={clearWalls}
              className="flex items-center gap-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg font-medium transition-all duration-200"
            >
              <StopIcon className="w-4 h-4" />
              Clear Walls
            </button>

            <button
              onClick={resetGrid}
              className="flex items-center gap-2 px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg font-medium transition-all duration-200"
            >
              <ArrowPathIcon className="w-4 h-4" />
              Reset Grid
            </button>
          </div>

          {dragMode && (
            <div className="mt-4 text-center">
              <p className="text-yellow-400 font-medium">
                {dragMode === 'start' ? 'Click anywhere to place the start node' : 'Click anywhere to place the target node'}
              </p>
            </div>
          )}
        </div>

        {/* Legend */}
        <div className="bg-gray-800/50 backdrop-blur-sm rounded-2xl p-4 mb-6 border border-gray-700">
          <div className="flex flex-wrap items-center justify-center gap-6 text-sm">
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 bg-start-node rounded shadow-glow-green"></div>
              <span className="text-gray-300">Start Node</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 bg-target-node rounded shadow-glow-amber"></div>
              <span className="text-gray-300">Target Node</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 bg-wall rounded"></div>
              <span className="text-gray-300">Wall</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 bg-matrix-cell border border-gray-700 rounded"></div>
              <span className="text-gray-300">Empty Cell</span>
            </div>
          </div>
        </div>

        {/* Matrix */}
        <div className="flex justify-center">
          <Matrix
            gridSize={GRID_SIZE}
            startPos={startPos}
            targetPos={targetPos}
            walls={walls}
            onCellClick={handleCellClick}
            onMouseDown={handleMouseDown}
            onMouseEnter={handleMouseEnter}
            onMouseUp={handleMouseUp}
            dragMode={dragMode}
          />
        </div>

        {/* Instructions */}
        <div className="mt-8 text-center text-gray-400 text-sm">
          <p>Click and drag to draw walls • Use the buttons above to move start and target nodes</p>
        </div>
      </div>
    </div>
  );
}

export default App;
