import React, { useState, useCallback } from 'react';
import { PlayIcon, StopIcon, ArrowPathIcon, CubeIcon, FlagIcon } from '@heroicons/react/24/solid';
import Matrix from './components/Matrix';

const GRID_SIZE = 20;

function App() {
  const [startPos, setStartPos] = useState({ row: 2, col: 2 });
  const [targetPos, setTargetPos] = useState({ row: 17, col: 17 });
  const [walls, setWalls] = useState(new Set());
  const [isDrawing, setIsDrawing] = useState(false);
  const [dragMode, setDragMode] = useState(null); // 'start', 'target', or null

  const handleCellClick = useCallback((row, col) => {
    if (dragMode === 'start') {
      setStartPos({ row, col });
      setDragMode(null);
    } else if (dragMode === 'target') {
      setTargetPos({ row, col });
      setDragMode(null);
    } else if (row === startPos.row && col === startPos.col) {
      // Clicked on start node
      return;
    } else if (row === targetPos.row && col === targetPos.col) {
      // Clicked on target node
      return;
    } else {
      // Toggle wall
      const key = `${row}-${col}`;
      setWalls(prev => {
        const newWalls = new Set(prev);
        if (newWalls.has(key)) {
          newWalls.delete(key);
        } else {
          newWalls.add(key);
        }
        return newWalls;
      });
    }
  }, [dragMode, startPos, targetPos]);

  const handleMouseDown = useCallback((row, col) => {
    if (row === startPos.row && col === startPos.col) {
      return;
    } else if (row === targetPos.row && col === targetPos.col) {
      return;
    } else {
      setIsDrawing(true);
      const key = `${row}-${col}`;
      setWalls(prev => {
        const newWalls = new Set(prev);
        if (!newWalls.has(key)) {
          newWalls.add(key);
        }
        return newWalls;
      });
    }
  }, [startPos, targetPos]);

  const handleMouseEnter = useCallback((row, col) => {
    if (isDrawing && row !== startPos.row && col !== startPos.col && 
        row !== targetPos.row && col !== targetPos.col) {
      const key = `${row}-${col}`;
      setWalls(prev => {
        const newWalls = new Set(prev);
        newWalls.add(key);
        return newWalls;
      });
    }
  }, [isDrawing, startPos, targetPos]);

  const handleMouseUp = useCallback(() => {
    setIsDrawing(false);
  }, []);

  const clearWalls = () => {
    setWalls(new Set());
  };

  const resetGrid = () => {
    setWalls(new Set());
    setStartPos({ row: 2, col: 2 });
    setTargetPos({ row: 17, col: 17 });
    setDragMode(null);
  };

  return (
    <div className="min-h-screen bg-gray-900 font-sans">
      {/* Header */}
      <header className="bg-gray-800 shadow-md py-4 px-6">
        <div className="max-w-7xl mx-auto flex justify-between items-center">
          <div className="flex items-center space-x-2">
            <CubeIcon className="h-8 w-8 text-start-node" />
            <h1 className="text-xl font-bold text-white">Matrix Visualizer</h1>
          </div>
          <div>
            <p className="text-gray-400 text-sm">Interactive 2D grid with movable nodes</p>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto py-6 px-4">
        {/* Controls Bar */}
        <div className="mb-6 bg-gray-800 rounded-lg shadow-lg p-4">
          <div className="flex flex-wrap justify-center gap-3">
            <button
              onClick={() => setDragMode(dragMode === 'start' ? null : 'start')}
              className={`flex items-center px-4 py-2 rounded-md font-medium text-sm transition-all ${dragMode === 'start' ? 'bg-start-node text-white' : 'bg-gray-700 text-gray-300 hover:bg-gray-600'}`}
            >
              <div className="w-3 h-3 bg-start-node rounded-full mr-2"></div>
              Move Start
            </button>

            <button
              onClick={() => setDragMode(dragMode === 'target' ? null : 'target')}
              className={`flex items-center px-4 py-2 rounded-md font-medium text-sm transition-all ${dragMode === 'target' ? 'bg-target-node text-white' : 'bg-gray-700 text-gray-300 hover:bg-gray-600'}`}
            >
              <div className="w-3 h-3 bg-target-node rounded-full mr-2"></div>
              Move Target
            </button>

            <button
              onClick={clearWalls}
              className="flex items-center px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-md font-medium text-sm transition-all"
            >
              <StopIcon className="w-4 h-4 mr-1" />
              Clear Walls
            </button>

            <button
              onClick={resetGrid}
              className="flex items-center px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-md font-medium text-sm transition-all"
            >
              <ArrowPathIcon className="w-4 h-4 mr-1" />
              Reset Grid
            </button>
          </div>

          {dragMode && (
            <div className="mt-4 flex justify-center">
              <p className="text-yellow-400 text-sm py-1 px-3 bg-yellow-900/20 rounded-md">
                {dragMode === 'start' ? 'Click anywhere to place the start node' : 'Click anywhere to place the target node'}
              </p>
            </div>
          )}
        </div>

        {/* Legend */}
        <div className="mb-6 bg-gray-800 rounded-lg shadow-lg p-4">
          <div className="flex justify-center flex-wrap gap-6">
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 bg-start-node rounded"></div>
              <span className="text-gray-300 text-sm">Start Node</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 bg-target-node rounded"></div>
              <span className="text-gray-300 text-sm">Target Node</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 bg-wall rounded"></div>
              <span className="text-gray-300 text-sm">Wall</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 bg-matrix-cell border border-gray-600 rounded"></div>
              <span className="text-gray-300 text-sm">Empty Cell</span>
            </div>
          </div>
        </div>

        {/* Matrix Container */}
        <div className="flex justify-center mb-6">
          <div className="bg-gray-800 rounded-lg shadow-lg p-6">
            <Matrix
              gridSize={GRID_SIZE}
              startPos={startPos}
              targetPos={targetPos}
              walls={walls}
              onCellClick={handleCellClick}
              onMouseDown={handleMouseDown}
              onMouseEnter={handleMouseEnter}
              onMouseUp={handleMouseUp}
              dragMode={dragMode}
            />
          </div>
        </div>

        {/* Settings/Statistics */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Settings Panel */}
          <div className="bg-gray-800 rounded-lg shadow-lg p-4">
            <h2 className="text-lg font-medium text-white mb-4 border-b border-gray-700 pb-2">Settings</h2>
            <div className="space-y-4">
              <div>
                <label className="block text-gray-400 text-sm mb-1">Maze solving algorithm</label>
                <select className="w-full bg-gray-700 text-white py-2 px-3 rounded-md cursor-pointer border border-gray-600">
                  <option>Breadth-First</option>
                  <option>Depth-First</option>
                  <option>A* Algorithm</option>
                  <option>Dijkstra's Algorithm</option>
                </select>
              </div>

              <div>
                <label className="block text-gray-400 text-sm mb-1">Maze generation algorithm</label>
                <select className="w-full bg-gray-700 text-white py-2 px-3 rounded-md cursor-pointer border border-gray-600">
                  <option>Custom</option>
                  <option>Random</option>
                  <option>Recursive Division</option>
                  <option>Prim's Algorithm</option>
                </select>
              </div>

              <button
                onClick={() => {}} // Placeholder for future functionality
                className="w-full mt-2 py-2 px-4 bg-green-600 hover:bg-green-700 text-white rounded-md font-medium transition-all flex items-center justify-center"
              >
                <PlayIcon className="w-4 h-4 mr-1" />
                Play
              </button>
            </div>
          </div>

          {/* Statistics Panel */}
          <div className="bg-gray-800 rounded-lg shadow-lg p-4">
            <h2 className="text-lg font-medium text-white mb-4 border-b border-gray-700 pb-2">Statistics</h2>
            <div className="grid grid-cols-2 gap-4">
              <div className="bg-gray-700/50 rounded-md p-3">
                <p className="text-xs text-gray-400">Grid Size</p>
                <p className="text-lg text-white font-mono">{GRID_SIZE} × {GRID_SIZE}</p>
              </div>
              <div className="bg-gray-700/50 rounded-md p-3">
                <p className="text-xs text-gray-400">Total Cells</p>
                <p className="text-lg text-white font-mono">{GRID_SIZE * GRID_SIZE}</p>
              </div>
              <div className="bg-gray-700/50 rounded-md p-3">
                <p className="text-xs text-gray-400">Walls</p>
                <p className="text-lg text-white font-mono">{walls.size}</p>
              </div>
              <div className="bg-gray-700/50 rounded-md p-3">
                <p className="text-xs text-gray-400">Start Position</p>
                <p className="text-lg text-start-node font-mono">({startPos.row}, {startPos.col})</p>
              </div>
              <div className="bg-gray-700/50 rounded-md p-3">
                <p className="text-xs text-gray-400">Target Position</p>
                <p className="text-lg text-target-node font-mono">({targetPos.row}, {targetPos.col})</p>
              </div>
            </div>
          </div>

          {/* Instructions */}
          <div className="bg-gray-800 rounded-lg shadow-lg p-4 mt-6 text-center">
            <p className="text-gray-400 text-sm">
              Click and drag to draw walls • Use the buttons above to move start and target nodes
            </p>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
