 */

/**
 * Generates a maze using Prim's algorithm
 * @param {number} gridSize - The size of the grid
 * @param {Function} updateWalls - Callback to update UI with current walls
 * @param {Function} updateMazeGeneration - Callback to update UI with maze generation cells
 * @param {number} animationSpeed - Speed of animation in milliseconds
 * @returns {Object} - Results including final walls set and generation stats
 */
export const generateMazeWithPrims = async (gridSize, updateWalls, updateMazeGeneration, animationSpeed = 100) => {
  const walls = new Set();

  // Create border walls
  for (let row = 0; row < gridSize; row++) {
    for (let col = 0; col < gridSize; col++) {
      if (row === 0 || row === gridSize - 1 || col === 0 || col === gridSize - 1) {
        walls.add(`${row}-${col}`);
      }
    }
  }

  // Add some random interior walls for demonstration
  for (let i = 0; i < gridSize * 2; i++) {
    const row = 1 + Math.floor(Math.random() * (gridSize - 2));
    const col = 1 + Math.floor(Math.random() * (gridSize - 2));
    walls.add(`${row}-${col}`);
  }
  updateWalls(new Set(walls));
  return {
    walls: new Set(walls),
    generationSteps: walls.size,
    visitedCells: gridSize * gridSize - walls.size
  };
};
export default generateMazeWithPrims;

