import React from 'react';
import { PlayIcon, CheckCircleIcon } from '@heroicons/react/24/solid';

const Matrix = ({
  gridSize = 20,
  startPos,
  targetPos,
  walls,
  onCellClick,
  onMouseDown,
  onMouseEnter,
  onMouseUp,
  dragMode,
  visitedCells = new Set(),
  pathCells = new Set(),
  mazeGenerationCells = new Set(),
  isVisualizing = false
}) => {
  // Calculate cell type (empty, wall, start, or target)
  const getCellType = (row, col) => {
    if (row === startPos.row && col === startPos.col) return 'start';
    if (row === targetPos.row && col === targetPos.col) return 'target';
    if (walls.has(`${row}-${col}`)) return 'wall';
    return 'empty';
  };

  // Check if we're in post-generation phase
  const isPostGeneration = mazeGenerationCells.size > 0 && walls.size > 0;

  // Get appropriate styles based on cell type
  const getCellStyle = (row, col) => {
    const type = getCellType(row, col);
    const cellKey = `${row}-${col}`;

    // During maze generation
    if (mazeGenerationCells.size > 0 && !isPostGeneration) {
      if (mazeGenerationCells.has(cellKey)) {
        // Show path being generated in black
        return {
          backgroundColor: '#000000', // Black for generating path
          width: '100%',
          height: '100%',
          aspectRatio: '1 / 1',
        };
      }
      // Keep other cells as they are
      const isEven = (row + col) % 2 === 0;
      return {
        backgroundColor: isEven ? '#2C3648' : '#1F2937',
        width: '100%',
        height: '100%',
        aspectRatio: '1 / 1',
      };
    }

    // After generation - apply color swap
    if (isPostGeneration) {
      if (walls.has(cellKey)) {
        // Walls become black
        return {
          backgroundColor: '#000000',
          width: '100%',
          height: '100%',
          aspectRatio: '1 / 1',
        };
      } else {
        // Paths become white
        return {
          backgroundColor: '#ffffff',
          width: '100%',
          height: '100%',
          aspectRatio: '1 / 1',
        };
      }
    }

    // Normal rendering when not in maze generation phase
    let backgroundColor = '#e2e8f0'; // Default light gray
    let animation = '';

    if (type === 'start') {
      backgroundColor = '#10b981'; // Green
    } else if (type === 'target') {
      backgroundColor = '#f59e0b'; // Amber
    } else if (type === 'wall') {
      backgroundColor = '#4b5563'; // Dark gray
    } else if (pathCells.has(cellKey)) {
      backgroundColor = '#3b82f6'; // Blue
      animation = 'pulse 1s infinite';
    } else if (visitedCells.has(cellKey)) {
      backgroundColor = '#0d9488'; // Teal
    } else {
      // Alternating colors for empty cells (chessboard pattern)
      const isEven = (row + col) % 2 === 0;
      backgroundColor = isEven ? '#2C3648' : '#1F2937';
    }

    return {
      backgroundColor,
      animation,
      width: '100%',
      height: '100%',
      aspectRatio: '1 / 1',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      transition: 'all 0.1s ease',
      cursor: getCellType(row, col) === 'start' || getCellType(row, col) === 'target' 
        ? 'grab' 
        : dragMode === 'start' || dragMode === 'target' ? 'grabbing' 
        : 'pointer',
      transform: getCellType(row, col) === 'start' || getCellType(row, col) === 'target' 
        ? 'scale(1.05)' 
        : 'scale(1)'
    };
  };

  // Render cell content (icons for start and target)
  const renderCellContent = (row, col) => {
    const type = getCellType(row, col);

    if (type === 'start') {
      return (
        <PlayIcon style={{ width: '60%', height: '60%', color: 'white' }} />
      );
    } else if (type === 'target') {
      return (
        <CheckCircleIcon style={{ width: '60%', height: '60%', color: 'white' }} />
      );
    }

    return null;
  };

  // Create all cells for the grid
  const renderCells = () => {
    const cells = [];

    for (let row = 0; row < gridSize; row++) {
      for (let col = 0; col < gridSize; col++) {
        cells.push(
          <div
            key={`${row}-${col}`}
            onClick={() => onCellClick(row, col)}
            onMouseDown={() => onMouseDown(row, col)}
            onMouseEnter={() => onMouseEnter(row, col)}
            onMouseUp={onMouseUp}
            onDragStart={(e) => e.preventDefault()}
            style={getCellStyle(row, col)}
          >
            {renderCellContent(row, col)}
          </div>
        );
      }
    }

    return cells;
  };

  // Render the matrix - padding is now handled by App.js grid container
  return (
    <div style={{
      width: '100%',
      height: '100%',
      display: 'grid',
      gridTemplateColumns: `repeat(${gridSize}, 1fr)`,
      gridTemplateRows: `repeat(${gridSize}, 1fr)`,
      gap: '1px',
      backgroundColor: '#374151',
      border: '1px solid #374151'
    }}>
      {renderCells()}
    </div>
  );
};

export default Matrix;
